import { createClient } from "@/lib/supabase/server";
import Link from "next/link";
import { User, Shield, ShieldAlert, ExternalLink } from "lucide-react";
import { UserRoleButton } from "@/components/admin/UserRoleButton";

interface Profile {
    id: string;
    nickname: string;
    level: number;
    balance: number;
    role: "user" | "admin" | "mod";
    created_at: string;
}

const roleConfig = {
    user: { label: "Usuário", color: "text-white/60", bg: "bg-white/5", icon: User },
    mod: { label: "Moderador", color: "text-blue-500", bg: "bg-blue-500/10", icon: Shield },
    admin: {
        label: "Admin",
        color: "text-brand-orange",
        bg: "bg-brand-orange/10",
        icon: ShieldAlert,
    },
};

export default async function AdminUsersPage() {
    const supabase = await createClient();

    const { data: profiles, error } = await supabase
        .from("profiles")
        .select("*")
        .order("created_at", { ascending: false });

    if (error) {
        return <div className="py-12 text-center text-white/40">Erro ao buscar usuários.</div>;
    }

    return (
        <div className="animate-in fade-in slide-in-from-bottom-4 duration-500">
            <div className="mb-8">
                <h2 className="text-3xl font-black tracking-tight uppercase italic">Usuários</h2>
                <p className="font-medium text-white/40">Gerencie os usuários do servidor.</p>
            </div>

            <div className="overflow-hidden rounded-3xl border border-white/5 bg-black/20 backdrop-blur-sm">
                <div className="overflow-x-auto">
                    <table className="w-full border-collapse text-left">
                        <thead className="bg-white/5 text-xs font-black tracking-widest text-white/40 uppercase">
                            <tr>
                                <th className="px-6 py-4">Usuário</th>
                                <th className="px-6 py-4">Nível</th>
                                <th className="px-6 py-4">Saldo</th>
                                <th className="px-6 py-4">Cargo</th>
                                <th className="px-6 py-4">Registro</th>
                                <th className="px-6 py-4 text-right">Ações</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-white/5">
                            {profiles?.map((profile: Profile) => {
                                const role = roleConfig[profile.role] || roleConfig.user;
                                const RoleIcon = role.icon;
                                return (
                                    <tr
                                        key={profile.id}
                                        className="group transition-colors hover:bg-white/[0.02]"
                                    >
                                        <td className="px-6 py-4">
                                            <div className="flex items-center gap-3">
                                                // eslint-disable-next-line @next/next/no-img-element
                                                <img
                                                    src={`https://mc-heads.net/avatar/${profile.nickname}/32`}
                                                    alt={profile.nickname}
                                                    className="h-10 w-10 rounded-xl border border-white/10"
                                                />
                                                <div>
                                                    <Link
                                                        href={`/player/${profile.nickname}`}
                                                        className="hover:text-brand-orange font-bold transition-colors"
                                                    >
                                                        {profile.nickname}
                                                    </Link>
                                                    <div className="font-mono text-xs text-white/40">
                                                        {profile.id.slice(0, 8)}...
                                                    </div>
                                                </div>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4">
                                            <span className="font-bold text-white/80">
                                                Nv. {profile.level}
                                            </span>
                                        </td>
                                        <td className="px-6 py-4 font-bold text-emerald-500">
                                            R$ {profile.balance?.toFixed(2) || "0.00"}
                                        </td>
                                        <td className="px-6 py-4">
                                            <div
                                                className={`inline-flex items-center gap-2 rounded-full px-3 py-1.5 ${role.bg} ${role.color}`}
                                            >
                                                <RoleIcon size={14} />
                                                <span className="text-xs font-bold uppercase">
                                                    {role.label}
                                                </span>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 text-sm text-white/60">
                                            {new Date(profile.created_at).toLocaleDateString(
                                                "pt-BR"
                                            )}
                                        </td>
                                        <td className="flex items-center justify-end gap-2 px-6 py-4 text-right">
                                            <Link
                                                href={`/player/${profile.nickname}`}
                                                className="rounded-lg bg-white/5 p-2 text-white/40 transition-all hover:bg-white/10 hover:text-white"
                                                title="Ver Perfil Público"
                                            >
                                                <ExternalLink size={16} />
                                            </Link>
                                            <UserRoleButton
                                                userId={profile.id}
                                                currentRole={profile.role}
                                            />
                                        </td>
                                    </tr>
                                );
                            })}
                            {(!profiles || profiles.length === 0) && (
                                <tr>
                                    <td
                                        colSpan={6}
                                        className="px-6 py-12 text-center text-white/20"
                                    >
                                        Nenhum usuário encontrado.
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

import type { Metadata } from "next";
import { Outfit } from "next/font/google";
import "./globals.css";
import { Suspense } from "react";

const outfit = Outfit({
    subsets: ["latin"],
    variable: "--font-outfit",
});

export const metadata: Metadata = {
    metadataBase: new URL("https://axissmp.com"),
    title: {
        default: "AxisSMP - A Nova Era do Survival",
        template: "%s | AxisSMP",
    },
    description:
        "Servidor de Minecraft Survival 1.21 com economia, quests, proteção de terrenos e uma comunidade ativa.",
    keywords: ["minecraft", "survival", "servidor", "1.21", "axissmp", "economia", "mcmmo"],
    authors: [{ name: "AxisSMP Team" }],
    openGraph: {
        type: "website",
        locale: "pt_BR",
        url: "https://axissmp.com",
        siteName: "AxisSMP",
        title: "AxisSMP - A Nova Era do Survival",
        description: "O melhor servidor survival 1.21. Economia, quests e diversão garantida!",
        images: [
            {
                url: "/images/site/og-image.jpg",
                width: 1200,
                height: 630,
                alt: "AxisSMP Server",
            },
        ],
    },
    twitter: {
        card: "summary_large_image",
        title: "AxisSMP - A Nova Era do Survival",
        description: "O melhor servidor survival 1.21.",
        images: ["/images/site/og-image.jpg"],
    },
    icons: {
        icon: [
            { url: "/favicon.ico" },
            { url: "/favicon-32x32.png", sizes: "32x32", type: "image/png" },
            { url: "/android-chrome-192x192.png", sizes: "192x192", type: "image/png" },
            { url: "/android-chrome-512x512.png", sizes: "512x512", type: "image/png" },
        ],
        shortcut: "/favicon.ico",
        apple: "/apple-touch-icon.png",
    },
    manifest: "/site.webmanifest",
};

import { CartProvider } from "@/context/CartContext";
import { Toaster } from "sonner";
import { Footer } from "@/components/layout/Footer";
import { ThemeProvider } from "@/components/providers/ThemeProvider";

export default function RootLayout({
    children,
}: Readonly<{
    children: React.ReactNode;
}>) {
    return (
        <html lang="pt-BR" suppressHydrationWarning style={{ scrollBehavior: "smooth" }}>
            <body
                className={`${outfit.variable} bg-background text-foreground flex min-h-screen flex-col font-sans antialiased`}
                suppressHydrationWarning
            >
                <ThemeProvider>
                    <Suspense fallback={null}>
                        <CartProvider>
                            <main className="flex-1">{children}</main>
                        </CartProvider>
                    </Suspense>
                </ThemeProvider>
                <Toaster
                    position="top-right"
                    richColors
                    theme="dark"
                    toastOptions={{
                        style: {
                            background: "rgba(0, 0, 0, 0.8)",
                            backdropFilter: "blur(12px)",
                            border: "1px solid rgba(255, 255, 255, 0.1)",
                            color: "white",
                        },
                    }}
                />
            </body>
        </html>
    );
}

"use client";

import { motion } from "framer-motion";
import { Trophy, Clock, ShoppingBag, Share2, ArrowLeft, CheckCircle2, Copy } from "lucide-react";
import Link from "next/link";
import { Navbar } from "@/components/layout/Navbar";
import { toast } from "sonner";
import ReactMarkdown from "react-markdown";

interface PublicProfile {
    nickname: string;
    level: number;
    created_at: string;
}

interface PlayerProfileClientProps {
    profile: PublicProfile;
}

export default function PlayerProfileClient({ profile }: PlayerProfileClientProps) {
    const handleShare = () => {
        const url = window.location.href;
        navigator.clipboard.writeText(url);
        toast.success("Link do perfil copiado!");
    };

    const copyIP = () => {
        navigator.clipboard.writeText("jogar.axissmp.com");
        toast.success("IP copiado! Entre no servidor.");
    };

    return (
        <main className="dark:bg-background selection:bg-brand-orange/30 min-h-screen bg-white text-slate-900 dark:text-white">
            <Navbar />

            <div className="relative z-10 mx-auto max-w-7xl px-6 pt-32 pb-20">
                {/* Header Actions */}
                <div className="mb-12 flex items-center justify-between">
                    <Link
                        href="/"
                        className="group hover:text-brand-orange flex items-center gap-2 text-sm font-bold text-slate-400 transition-colors dark:text-slate-500"
                    >
                        <ArrowLeft
                            size={16}
                            className="transition-transform group-hover:-translate-x-1"
                        />
                        Ver outros jogadores
                    </Link>

                    <button
                        onClick={handleShare}
                        className="flex items-center gap-2 rounded-2xl border border-slate-200 bg-slate-50 px-6 py-3 text-sm font-bold text-slate-600 shadow-sm transition-all hover:bg-slate-100 active:scale-95 dark:border-white/10 dark:bg-white/5 dark:text-slate-300 dark:hover:bg-white/10"
                    >
                        <Share2 size={18} />
                        Compartilhar Perfil
                    </button>
                </div>

                {/* Profile Hero */}
                <div className="grid grid-cols-1 items-stretch gap-8 lg:grid-cols-12">
                    {/* Character Visualization */}
                    <motion.div
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="relative flex min-h-[500px] flex-col items-center justify-center overflow-hidden rounded-[2.5rem] border border-slate-100 bg-slate-50 p-12 lg:col-span-7 dark:border-white/10 dark:bg-white/5"
                    >
                        <div className="absolute top-8 left-8 text-left">
                            <span className="mb-2 block text-[10px] font-black tracking-[0.2em] text-slate-300 uppercase dark:text-slate-600">
                                Visual 3D Atual
                            </span>
                            <div className="flex items-center gap-2">
                                <h1 className="text-5xl font-black tracking-tighter text-slate-900 uppercase italic md:text-6xl dark:text-white">
                                    {profile.nickname}
                                </h1>
                                <CheckCircle2 size={24} className="text-brand-orange mt-2" />
                            </div>
                        </div>

                        {/* Character Render */}
                        <div className="relative mt-12 h-[400px] w-full cursor-zoom-in transition-transform duration-700 hover:scale-110">
                            <img
                                src={`https://mc-heads.net/body/${profile.nickname}`}
                                alt={`${profile.nickname} character`}
                                className="h-full w-full object-contain drop-shadow-[0_0_50px_rgba(255,145,0,0.1)] filter"
                                loading="eager"
                            />
                        </div>

                        <div className="absolute right-8 bottom-8">
                            <div className="bg-brand-orange/10 text-brand-orange border-brand-orange/20 rounded-full border px-4 py-1.5 text-[10px] font-black tracking-widest uppercase">
                                Jogador do AxisSMP
                            </div>
                        </div>
                    </motion.div>

                    {/* Stats & Info */}
                    <div className="flex flex-col gap-6 lg:col-span-5">
                        {/* Level Card */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.1 }}
                            className="bg-brand-orange group shadow-brand-orange/20 relative overflow-hidden rounded-[2.5rem] p-8 text-white shadow-lg"
                        >
                            <div className="absolute top-0 right-0 p-8 opacity-20 transition-transform group-hover:scale-110">
                                <Trophy size={80} />
                            </div>
                            <span className="mb-1 block text-[10px] font-black tracking-widest uppercase opacity-60">
                                Nível Global
                            </span>
                            <div className="flex items-baseline gap-2">
                                <span className="text-7xl font-black tracking-tighter italic">
                                    {profile.level}
                                </span>
                                <span className="text-sm font-black tracking-widest uppercase opacity-40">
                                    Rank #---
                                </span>
                            </div>
                        </motion.div>

                        {/* Summary Stats */}
                        <div className="grid grid-cols-2 gap-6">
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 0.2 }}
                                className="rounded-[2rem] border border-slate-100 bg-slate-50 p-6 shadow-sm dark:border-white/10 dark:bg-white/5"
                            >
                                <div className="mb-4 w-fit rounded-xl border border-slate-100 bg-white p-3 text-slate-400 dark:border-white/5 dark:bg-white/10 dark:text-slate-300">
                                    <Clock size={20} />
                                </div>
                                <span className="mb-1 block text-[10px] font-black tracking-widest text-slate-400 uppercase dark:text-slate-500">
                                    Jogando desde
                                </span>
                                <p className="text-xl font-black text-slate-800 dark:text-white">
                                    {new Date(profile.created_at).toLocaleDateString("pt-BR", {
                                        month: "short",
                                        year: "numeric",
                                    })}
                                </p>
                            </motion.div>

                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 0.3 }}
                                className="rounded-[2rem] border border-slate-100 bg-slate-50 p-6 shadow-sm dark:border-white/10 dark:bg-white/5"
                            >
                                <div className="mb-4 w-fit rounded-xl border border-slate-100 bg-white p-3 text-slate-400 dark:border-white/5 dark:bg-white/10 dark:text-slate-300">
                                    <ShoppingBag size={20} />
                                </div>
                                <span className="mb-1 block text-[10px] font-black tracking-widest text-slate-400 uppercase dark:text-slate-500">
                                    Apoio na Loja
                                </span>
                                <p className="text-xl font-black text-slate-800 dark:text-white">
                                    ---
                                </p>
                            </motion.div>
                        </div>

                        {/* Action Card */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.4 }}
                            className="flex flex-1 flex-col justify-between rounded-[2.5rem] border border-slate-100 bg-slate-50 p-8 shadow-sm dark:border-white/10 dark:bg-white/5"
                        >
                            <div>
                                <h3 className="mb-4 text-2xl font-black tracking-tight text-slate-900 uppercase italic dark:text-white">
                                    Conecte-se com {profile.nickname}
                                </h3>
                                <div className="prose prose-p:leading-relaxed prose-strong:text-slate-900 dark:prose-strong:text-white prose-strong:font-black mb-6 text-sm leading-relaxed text-slate-500 dark:text-slate-400">
                                    <ReactMarkdown>
                                        {`Este é o perfil oficial de ${profile.nickname} no servidor **AxisSMP**. Entre agora para jogar junto ou ver suas conquistas in-game!`}
                                    </ReactMarkdown>
                                </div>
                            </div>

                            <div className="space-y-3">
                                <Link
                                    href="/store"
                                    className="flex w-full items-center justify-center gap-3 rounded-2xl bg-slate-900 py-4 text-sm font-black tracking-wider text-white uppercase shadow-lg shadow-slate-900/10 transition-all hover:bg-slate-800 dark:bg-white dark:text-slate-900 dark:hover:bg-slate-200"
                                >
                                    Enviar Presente
                                    <ShoppingBag size={18} />
                                </Link>
                                <button
                                    onClick={copyIP}
                                    className="flex w-full items-center justify-center gap-3 rounded-2xl border-2 border-slate-200 bg-transparent py-4 text-sm font-black tracking-wider text-slate-900 uppercase transition-all hover:bg-slate-100 dark:border-white/20 dark:text-white dark:hover:bg-white/10"
                                >
                                    Copiar IP do Servidor
                                    <Copy size={18} />
                                </button>
                            </div>
                        </motion.div>
                    </div>
                </div>

                {/* Footer Decor */}
                <div className="mt-20 text-center">
                    <div className="mb-8 h-px w-full bg-gradient-to-r from-transparent via-slate-200 to-transparent" />
                    <p className="text-[10px] font-black tracking-[0.3em] text-slate-300 uppercase">
                        AxisSMP Official Community Profiles
                    </p>
                </div>
            </div>
        </main>
    );
}

"use client";

import { motion } from "framer-motion";
import { Check, X, Shield, AlertTriangle, MessageSquare, Heart, Ban, Gavel } from "lucide-react";
import { cn } from "@/lib/utils";
import { Navbar } from "@/components/layout/Navbar";
import { Footer } from "@/components/layout/Footer";

const allowedRules = [
    {
        title: "Respeito e Comunidade",
        description:
            "Trate todos os jogadores e staff com respeito. Mantenha um ambiente amigável e acolhedor para todos.",
        icon: Heart,
    },
    {
        title: "Fair Play",
        description:
            "Jogue limpo. O uso de mods que apenas melhoram a performance ou estética é permitido.",
        icon: Shield,
    },
    {
        title: "Reportar Bugs",
        description:
            "Encontrou um erro? Reporte imediatamente à staff. O abuso de bugs para vantagem própria é proibido.",
        icon: AlertTriangle,
    },
    {
        title: "Comércio Justo",
        description:
            "Negocie itens e serviços de forma honesta. Golpes e fraudes não são tolerados.",
        icon: MessageSquare,
    },
];

const prohibitedRules = [
    {
        title: "Cheating e Hacks",
        description:
            "O uso de qualquer cliente alternativo (hacks), macros ou automações que dêem vantagem desleal é estritamente proibido.",
        icon: Ban,
    },
    {
        title: "Divulgação",
        description:
            "Não divulgue outros servidores, canais ou links suspeitos no chat global ou privado.",
        icon: MessageSquare,
    },
    {
        title: "Discurso de Ódio",
        description:
            "Racismo, homofobia, sexismo ou qualquer forma de discriminação resultará em banimento imediato.",
        icon: Gavel,
    },
    {
        title: "Griefing Desnecessário",
        description:
            "A destruição massiva de mapas ou construções de outros jogadores sem contexto de guerra/pvp permitido.",
        icon: X,
    },
];

export default function RulesPage() {
    return (
        <main className="bg-background relative min-h-screen overflow-hidden transition-colors duration-300">
            {/* Background Elements */}
            <div className="pointer-events-none fixed inset-0">
                <div className="bg-brand-orange/10 dark:bg-brand-orange/5 absolute top-0 right-0 h-[500px] w-[500px] translate-x-1/2 -translate-y-1/2 rounded-full blur-[100px]" />
                <div className="bg-brand-blue/10 dark:bg-brand-blue/5 absolute bottom-0 left-0 h-[500px] w-[500px] -translate-x-1/2 translate-y-1/2 rounded-full blur-[100px]" />
                <div className="absolute top-1/2 left-1/2 h-full w-full -translate-x-1/2 -translate-y-1/2 bg-slate-900/5 opacity-[0.03] dark:bg-white/5" />
            </div>

            <Navbar />

            <div className="relative mx-auto max-w-7xl px-6 pt-32 pb-20 md:px-8">
                {/* Header */}
                <div className="mb-20 space-y-4 text-center">
                    <motion.h1
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="text-brand-dark text-5xl font-black tracking-tight md:text-7xl dark:text-white"
                    >
                        REGRAS DO <span className="text-brand-orange">AXIS</span>
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 }}
                        className="text-brand-dark/80 mx-auto max-w-2xl text-xl font-medium dark:text-white/80"
                    >
                        A transparência é a chave. Entenda o que faz nossa comunidade girar e o que
                        a mantém segura para todos.
                    </motion.p>
                </div>

                {/* Content Split */}
                <div className="grid gap-8 md:grid-cols-2 md:gap-16">
                    {/* Allowed Column */}
                    <div className="space-y-8">
                        <motion.div
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: 0.2 }}
                            className="mb-8 flex items-center gap-4"
                        >
                            <div className="flex h-12 w-12 items-center justify-center rounded-2xl bg-green-500/10 text-green-600">
                                <Check size={24} strokeWidth={3} />
                            </div>
                            <h2 className="text-brand-dark text-3xl font-bold dark:text-white">
                                Permitido
                            </h2>
                        </motion.div>

                        <div className="space-y-4">
                            {allowedRules.map((rule, index) => (
                                <motion.div
                                    key={rule.title}
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: 0.3 + index * 0.1 }}
                                    className="group border-brand-dark/5 relative rounded-2xl border bg-white p-6 transition-all duration-300 hover:-translate-y-1 hover:shadow-lg hover:shadow-green-500/5 dark:border-white/10 dark:bg-white/5 dark:hover:bg-white/10 dark:hover:shadow-none"
                                >
                                    <div className="absolute top-0 left-0 h-full w-1 rounded-l-2xl bg-green-500 opacity-0 transition-opacity group-hover:opacity-100" />
                                    <div className="flex gap-4">
                                        <div className="bg-brand-light text-brand-dark/40 flex h-10 w-10 items-center justify-center rounded-xl transition-colors group-hover:bg-green-50 group-hover:text-green-600 dark:bg-white/5 dark:text-white/40 dark:group-hover:bg-green-500/10">
                                            <rule.icon size={20} />
                                        </div>
                                        <div>
                                            <h3 className="text-brand-dark mb-1 text-lg font-bold transition-colors group-hover:text-green-700 dark:text-white dark:group-hover:text-green-500">
                                                {rule.title}
                                            </h3>
                                            <p className="text-brand-dark/80 text-sm leading-relaxed font-medium dark:text-white/70">
                                                {rule.description}
                                            </p>
                                        </div>
                                    </div>
                                </motion.div>
                            ))}
                        </div>
                    </div>

                    {/* Prohibited Column */}
                    <div className="space-y-8">
                        <motion.div
                            initial={{ opacity: 0, x: 20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: 0.2 }}
                            className="mb-8 flex items-center gap-4"
                        >
                            <div className="flex h-12 w-12 items-center justify-center rounded-2xl bg-red-500/10 text-red-600">
                                <X size={24} strokeWidth={3} />
                            </div>
                            <h2 className="text-brand-dark text-3xl font-bold dark:text-white">
                                Proibido
                            </h2>
                        </motion.div>

                        <div className="space-y-4">
                            {prohibitedRules.map((rule, index) => (
                                <motion.div
                                    key={rule.title}
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: 0.3 + index * 0.1 }}
                                    className="group border-brand-dark/5 relative rounded-2xl border bg-white p-6 transition-all duration-300 hover:-translate-y-1 hover:shadow-lg hover:shadow-red-500/5 dark:border-white/10 dark:bg-white/5 dark:hover:bg-white/10 dark:hover:shadow-none"
                                >
