"use client";

import { useState, useRef } from "react";
import { createClient } from "@/lib/supabase/client";
import { Upload, X, Loader2, Image as ImageIcon } from "lucide-react";
import { toast } from "sonner";

interface ImageUploadProps {
    value: string;
    onChange: (url: string) => void;
    bucket?: string;
    folder?: string;
}

export function ImageUpload({
    value,
    onChange,
    bucket = "product-images",
    folder = "products",
}: ImageUploadProps) {
    const [isUploading, setIsUploading] = useState(false);
    const [isDragging, setIsDragging] = useState(false);
    const inputRef = useRef<HTMLInputElement>(null);
    const supabase = createClient();

    const handleFileSelect = async (file: File) => {
        if (!file.type.startsWith("image/")) {
            toast.error("Apenas imagens são permitidas.");
            return;
        }

        if (file.size > 5 * 1024 * 1024) {
            toast.error("Imagem muito grande. Máximo: 5MB");
            return;
        }

        setIsUploading(true);

        const fileExt = file.name.split(".").pop();
        const fileName = `${folder}/${Date.now()}-${Math.random().toString(36).substring(7)}.${fileExt}`;

        const { error } = await supabase.storage.from(bucket).upload(fileName, file, {
            cacheControl: "3600",
            upsert: false,
        });

        if (error) {
            console.error("Upload error:", error);
            toast.error("Erro ao fazer upload. Verifique se você é admin.");
            setIsUploading(false);
            return;
        }

        const { data: publicUrl } = supabase.storage.from(bucket).getPublicUrl(fileName);

        onChange(publicUrl.publicUrl);
        toast.success("Imagem enviada com sucesso!");
        setIsUploading(false);
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;
        handleFileSelect(file);
    };

    const handleDrop = (e: React.DragEvent) => {
        e.preventDefault();
        setIsDragging(false);

        const file = e.dataTransfer.files?.[0];
        if (!file) return;
        handleFileSelect(file);
    };

    const handleDragOver = (e: React.DragEvent) => {
        e.preventDefault();
        setIsDragging(true);
    };

    const handleDragLeave = () => {
        setIsDragging(false);
    };

    const handleRemove = () => {
        onChange("");
    };

    if (value) {
        return (
            <div className="group relative">
                <div className="relative aspect-video w-full overflow-hidden rounded-2xl border-2 border-white/5 bg-black/20 backdrop-blur-sm">
                    <img src={value} alt="Preview" className="h-full w-full object-contain" />
                    <div className="absolute inset-0 flex items-center justify-center gap-4 bg-black/60 opacity-0 transition-opacity group-hover:opacity-100">
                        <button
                            type="button"
                            onClick={() => inputRef.current?.click()}
                            className="rounded-xl bg-white/10 p-3 text-white transition-colors hover:bg-white/20"
                            title="Trocar imagem"
                        >
                            <Upload size={20} />
                        </button>
                        <button
                            type="button"
                            onClick={handleRemove}
                            className="rounded-xl bg-red-500/20 p-3 text-red-400 transition-colors hover:bg-red-500/40"
                            title="Remover imagem"
                        >
                            <X size={20} />
                        </button>
                    </div>
                </div>
                <input
                    ref={inputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleInputChange}
                    className="hidden"
                />
            </div>
        );
    }

    return (
        <div
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onClick={() => inputRef.current?.click()}
            className={`relative flex aspect-video w-full cursor-pointer flex-col items-center justify-center gap-4 rounded-2xl border-2 border-dashed transition-all ${
                isDragging
                    ? "border-brand-orange bg-brand-orange/10"
                    : "border-white/5 bg-black/20 hover:border-white/10 hover:bg-black/30"
            } ${isUploading ? "pointer-events-none opacity-50" : ""} `}
        >
            {isUploading ? (
                <>
                    <Loader2 className="text-brand-orange animate-spin" size={40} />
                    <span className="text-sm font-medium text-white/60">Enviando...</span>
                </>
            ) : (
                <>
                    <div className="rounded-2xl bg-white/5 p-4 text-white/40">
                        <ImageIcon size={32} />
                    </div>
                    <div className="text-center">
                        <p className="text-sm font-bold text-white/80">Clique para fazer upload</p>
                        <p className="mt-1 text-xs text-white/40">
                            ou arraste uma imagem aqui (máx. 5MB)
                        </p>
                    </div>
                </>
            )}
            <input
                ref={inputRef}
                type="file"
                accept="image/*"
                onChange={handleInputChange}
                className="hidden"
            />
        </div>
    );
}

"use client";

import { createPost, updatePost, getPost } from "@/lib/actions/posts";
import { useRouter } from "next/navigation";
import { useEffect, useState, useTransition } from "react";
import { toast } from "sonner";
import { ArrowLeft, Save, Loader2, Eye, Edit3 } from "lucide-react";
import Link from "next/link";
// Using react-markdown if available would be great for preview, but sticking to basics for now to avoid dependency issues.
// We will just have a textarea.

interface PostFormProps {
    slugParam: string; // "new" or actual slug
}

export default function PostForm({ slugParam }: PostFormProps) {
    const router = useRouter();
    const [isPending, startTransition] = useTransition();
    const [loading, setLoading] = useState(slugParam !== "new");

    // Form states
    const [slug, setSlug] = useState("");
    const [title, setTitle] = useState("");
    const [excerpt, setExcerpt] = useState("");
    const [content, setContent] = useState("");
    const [category, setCategory] = useState("update");
    const [author, setAuthor] = useState("Admin");
    const [image, setImage] = useState("");

    // Preview mode
    const [previewMode, setPreviewMode] = useState(false);

    useEffect(() => {
        if (slugParam !== "new") {
            const fetchPost = async () => {
                const { data, error } = await getPost(decodeURIComponent(slugParam));
                if (error) {
                    toast.error(error);
                    router.push("/admin/posts");
                    return;
                }
                if (data) {
                    setSlug(data.slug);
                    setTitle(data.title);
                    setExcerpt(data.excerpt);
                    setContent(data.content);
                    setCategory(data.category);
                    setAuthor(data.author);
                    setImage(data.image || "");
                }
                setLoading(false);
            };
            fetchPost();
        }
    }, [slugParam, router]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set("slug", slug);
        formData.set("title", title);
        formData.set("excerpt", excerpt);
        formData.set("content", content);
        formData.set("category", category);
        formData.set("author", author);
        formData.set("image", image);

        startTransition(async () => {
            const result =
                slugParam === "new"
                    ? await createPost(formData)
                    : await updatePost(slugParam, formData);

            if (result.error) {
                toast.error(result.error);
                return;
            }

            toast.success(slugParam === "new" ? "Notícia criada!" : "Notícia atualizada!");
            router.push("/admin/posts");
            router.refresh();
        });
    };

    // Auto-generate slug from title if new and slug is empty
    const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const val = e.target.value;
        setTitle(val);
        if (slugParam === "new" && !slug) {
            setSlug(
                val
                    .toLowerCase()
                    .replace(/[^a-z0-9]+/g, "-")
                    .replace(/(^-|-$)/g, "")
            );
        }
    };

    if (loading) {
        return (
            <div className="flex h-64 items-center justify-center">
                <Loader2 className="text-brand-orange animate-spin" size={32} />
            </div>
        );
    }

    return (
        <div className="animate-in fade-in slide-in-from-bottom-4 mx-auto max-w-4xl duration-500">
            <Link
                href="/admin/posts"
                className="mb-6 inline-flex items-center gap-2 text-sm font-bold text-white/40 transition-colors hover:text-white"
            >
                <ArrowLeft size={16} />
                Voltar para Notícias
            </Link>

            <div className="rounded-3xl border border-white/10 bg-white/5 p-8">
                <div className="mb-8 flex items-center justify-between">
                    <h2 className="text-2xl font-black tracking-tight uppercase italic">
                        {slugParam === "new" ? "Nova Notícia" : "Editar Notícia"}
                    </h2>
                    <div className="flex rounded-lg border border-white/10 bg-black/20 p-1">
                        <button
                            type="button"
                            onClick={() => setPreviewMode(false)}
                            className={`rounded-md px-3 py-1.5 text-xs font-bold uppercase transition-all ${!previewMode ? "bg-white/10 text-white shadow-sm" : "text-white/40 hover:text-white"}`}
                        >
                            Editar
                        </button>
                    </div>
                </div>

                <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
                        {/* Title */}
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Título
                            </label>
                            <input
                                type="text"
                                value={title}
                                onChange={handleTitleChange}
                                required
                                className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                                placeholder="Título da notícia"
                            />
                        </div>

                        {/* Slug */}
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Slug (URL)
                            </label>
                            <input
                                type="text"
                                value={slug}
                                onChange={(e) => setSlug(e.target.value)}
                                required
                                readOnly={slugParam !== "new"} // Maybe allow edit but warn? Sticking to readonly for edit to avoid dead links
                                className={`focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none ${slugParam !== "new" ? "cursor-not-allowed opacity-50" : ""}`}
                                placeholder="titulo-da-noticia"
                            />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 gap-6 md:grid-cols-3">
                        {/* Category */}
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Categoria
                            </label>
                            <select
                                value={category}
                                onChange={(e) => setCategory(e.target.value)}
                                className="focus:border-brand-orange focus:ring-brand-orange w-full cursor-pointer appearance-none rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all focus:ring-1 focus:outline-none"
                            >
                                <option value="update">Update</option>
                                <option value="event">Evento</option>
                                <option value="maintenance">Manutenção</option>
                                <option value="announcement">Anúncio</option>
                            </select>
                        </div>

                        {/* Author */}
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Autor
                            </label>
                            <input
                                type="text"
                                value={author}
                                onChange={(e) => setAuthor(e.target.value)}
                                className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                            />
                        </div>

                        {/* Image */}
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Imagem Capa (URL)
                            </label>
                            <input
                                type="text"
                                value={image}
                                onChange={(e) => setImage(e.target.value)}
                                className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                                placeholder="Opcional"
                            />
                        </div>
                    </div>

                    {/* Excerpt */}
                    <div className="space-y-2">
                        <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                            Resumo
                        </label>
                        <textarea
                            value={excerpt}
                            onChange={(e) => setExcerpt(e.target.value)}
                            required
                            rows={2}
                            className="focus:border-brand-orange focus:ring-brand-orange w-full resize-none rounded-xl border border-white/10 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                            placeholder="Um breve resumo que aparecerá nos cards..."
                        />
                    </div>

                    {/* Content */}
                    <div className="space-y-2">
                        <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                            Conteúdo (Markdown)
                        </label>
                        <textarea
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                            required
                            rows={15}
                            className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/10 bg-black/20 px-4 py-3 font-mono text-sm text-white/80 transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                            placeholder="# Título Principal&#10;&#10;Seu texto aqui. **Negrito**, *itálico*, etc."
                        />
                        <p className="text-xs text-white/20">Suporta formatação Markdown básica.</p>
                    </div>

                    <div className="flex justify-end border-t border-white/10 pt-4">
                        <button
                            type="submit"
                            disabled={isPending}
                            className="bg-brand-orange text-brand-dark flex items-center gap-2 rounded-xl px-8 py-3 text-sm font-black uppercase transition-all hover:scale-[1.02] active:scale-95 disabled:cursor-not-allowed disabled:opacity-50"
                        >
                            {isPending ? (
                                <Loader2 className="animate-spin" size={18} />
                            ) : (
                                <Save size={18} />
                            )}
                            {slugParam === "new" ? "Publicar Notícia" : "Salvar Alterações"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

"use client";

import { createProduct, updateProduct, getProduct } from "@/lib/actions/products";
import { useRouter } from "next/navigation";
import { useEffect, useState, useTransition } from "react";
import { toast } from "sonner";
import { ArrowLeft, Save, Loader2 } from "lucide-react";
import Link from "next/link";
import { ImageUpload } from "@/components/admin/ImageUpload";

interface ProductFormProps {
    id: string; // "new" or uuid
}

interface Product {
    id: string;
    name: string;
    price: number;
    category: string;
    image: string;
    color: string;
}

export default function ProductForm({ id }: ProductFormProps) {
    const router = useRouter();
    const [isPending, startTransition] = useTransition();
    const [loading, setLoading] = useState(id !== "new");
    const [product, setProduct] = useState<Product | null>(null);

    // Form states
    const [name, setName] = useState("");
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState("vips");
    const [image, setImage] = useState("");
    const [color, setColor] = useState("bg-brand-orange/20");

    useEffect(() => {
        if (id === "new") return;

        const fetchProduct = async () => {
            const { data, error } = await getProduct(id);

            if (error) {
                toast.error(error);
                router.push("/admin/products");
                return;
            }

            if (!data) {
                setLoading(false);
                return;
            }

            setProduct(data);
            setName(data.name);
            setPrice(data.price.toString());
            setCategory(data.category);
            setImage(data.image);
            setColor(data.color || "bg-brand-orange/20");
            setLoading(false);
        };

        fetchProduct();
    }, [id, router]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set("name", name);
        formData.set("price", price);
        formData.set("category", category);
        formData.set("image", image);
        formData.set("color", color);

        startTransition(async () => {
            const result =
                id === "new" ? await createProduct(formData) : await updateProduct(id, formData);

            if (result.error) {
                toast.error(result.error);
                return;
            }

            toast.success(id === "new" ? "Produto criado!" : "Produto atualizado!");
            router.push("/admin/products");
            router.refresh();
        });
    };

    if (loading) {
        return (
            <div className="flex h-64 items-center justify-center">
                <Loader2 className="text-brand-orange animate-spin" size={32} />
            </div>
        );
    }

    return (
        <div className="animate-in fade-in slide-in-from-bottom-4 mx-auto max-w-2xl duration-500">
            <Link
                href="/admin/products"
                className="mb-6 inline-flex items-center gap-2 text-sm font-bold text-white/40 transition-colors hover:text-white"
            >
                <ArrowLeft size={16} />
                Voltar para Produtos
            </Link>

            <div className="rounded-3xl border border-white/5 bg-black/20 p-8 backdrop-blur-sm">
                <h2 className="mb-8 text-2xl font-black tracking-tight uppercase italic">
                    {id === "new" ? "Novo Produto" : "Editar Produto"}
                </h2>

                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* Name */}
                    <div className="space-y-2">
                        <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                            Nome do Produto
                        </label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                            className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/5 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                            placeholder="Ex: VIP Lendário"
                        />
                    </div>

                    {/* Price and Category */}
                    <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Preço (R$)
                            </label>
                            <input
                                type="number"
                                step="0.01"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                required
                                className="focus:border-brand-orange focus:ring-brand-orange w-full rounded-xl border border-white/5 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                                placeholder="0.00"
                            />
                        </div>
                        <div className="space-y-2">
                            <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                                Categoria
                            </label>
                            <select
                                value={category}
                                onChange={(e) => setCategory(e.target.value)}
                                className="focus:border-brand-orange focus:ring-brand-orange w-full cursor-pointer appearance-none rounded-xl border border-white/5 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all focus:ring-1 focus:outline-none"
                            >
                                <option value="vips">VIPs</option>
                                <option value="coins">Coins</option>
                                <option value="unban">Unban</option>
                            </select>
                        </div>
                    </div>

                    {/* Image Upload */}
                    <div className="space-y-2">
                        <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                            Imagem do Produto
                        </label>
                        <ImageUpload value={image} onChange={setImage} />
                    </div>

                    {/* Color Class */}
                    <div className="space-y-2">
                        <label className="text-xs font-black tracking-widest text-white/40 uppercase">
                            Cor de Fundo (Tailwind Class)
                        </label>
                        <div className="flex gap-4">
                            <input
                                type="text"
                                value={color}
                                onChange={(e) => setColor(e.target.value)}
                                className="focus:border-brand-orange focus:ring-brand-orange flex-1 rounded-xl border border-white/5 bg-black/20 px-4 py-3 text-sm font-medium text-white transition-all placeholder:text-white/20 focus:ring-1 focus:outline-none"
                                placeholder="bg-brand-orange/20"
                            />
                            <div
                                className={`h-12 w-12 rounded-xl border border-white/5 ${color} shrink-0`}
                            ></div>
                        </div>
                    </div>

                    <div className="flex justify-end border-t border-white/5 pt-4">
                        <button
                            type="submit"
                            disabled={isPending}
                            className="bg-brand-orange text-brand-dark flex items-center gap-2 rounded-xl px-8 py-3 text-sm font-black uppercase transition-all hover:scale-[1.02] active:scale-95 disabled:cursor-not-allowed disabled:opacity-50"
                        >
                            {isPending ? (
                                <Loader2 className="animate-spin" size={18} />
                            ) : (
                                <Save size={18} />
                            )}
                            {id === "new" ? "Criar Produto" : "Salvar Alterações"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

"use client";

